import React,{useState} from 'react';
import './App.css';
import Alert from './components/Alert';
import Navbar from './components/Navbar';

function App() {


  const [alert, setalert]=useState(null)


  const showAlert = (massage,type)=>{
    setalert({
      msg : massage,
      type : type
    })
  }
  return (
    <>
      <Navbar About="About Us" />
      <Alert alert={alert}/>
    </>

  );
}

export default App;
